for i in range(1, 7):
    if i % 2 == 0 ① i % 3 == 0:
        print(f"{i}は6の倍数です")
    elif i % 2 == 0 ② i % 3 == 0:
        print(f"{i}は2か3の倍数です")
    ③ :
        print(f"{i}は2の倍数でも3の倍数でもありません")

